actionConfiguration.body:
() => {
  storeValue("customer_data", []);
  var data = getDataSources.data.values.map(row => {
    return row;
  });
  storeValue("customer_data", data);
  var dataSourceOptions = data.map(row => {
    var accounts = row.accounts.map(row => {
      return {
        'label': row,
        'value': row
      };
    });
    storeValue(`dataSource_${row.name}_accounts`, accounts);
    var dataServices = row.dataServices.names.map(row => {
      return {
        'label': row,
        'value': row
      };
    });
    storeValue(`dataSource_${row.name}_dataServices`, dataServices);
    return {
      'label': row.name,
      'value': row.name
    };
  });
  storeValue("dataSourceOptions", dataSourceOptions);
}

