actionConfiguration.body:
() => {
  let data = getMetrics.data;
  if (!data || !data.dailyMetrics) {
    return [];
  }
  data = data.dailyMetrics;
  let graphData = [];
  for (let day of data) {
    graphData.push({
      x: day.date,
      y: day.totaloperations
    });
  }
  return graphData;
}

