actionConfiguration.body:
() => {
  var data = getEnrollmentsData.data;
  if (!(('items' in data))) {
    return [];
  }
  data = data.items;
  var formattedData = [];
  for (const row of data) {
    var rowData = {};
    for (const field of EnrollmentsDataFields.selectedOptionValues) {
      if ((field in row)) {
        rowData[field] = row[field];
      } else {
        rowData[field] = row["data"][field];
      }
      if (rowData[field] instanceof Array) {
        rowData[field] = rowData[field].join();
      }
      if (field == 'updatedat' || field == 'createdat') {
        let date = new Date(rowData[field]);
        rowData[field] = date.toLocaleString();
      }
    }
    formattedData.push(rowData);
  }
  return formattedData;
}

