actionConfiguration.body:
() => {
  var row = EnrollmentsData.selectedRowIndex;
  var data = getEnrollmentsData.data.items[row];
  var formattedText = "";
  for (var item in data) {
    if (data[item] instanceof Object) {
      const obj = data[item];
      for (var data_item in obj) {
        formattedText += "<b>" + data_item + ":</b> " + JSON.stringify(obj[data_item]) + "<br/>";
      }
    } else {
      formattedText += "<b>" + item + ":</b> " + JSON.stringify(data[item]) + "<br/>";
    }
  }
  return formattedText;
}

