actionConfiguration.body:
driverId => {
  if (driverId.length == 0) {
    showAlert("Driver ID cannot be empty!", 'error');
    return;
  }
  var data = appsmith.store['updateVehicleData'];
  data.driverIds.push(driverId);
  storeValue('updateVehicleData', data);
  closeModal('addDriver');
}

