children.tableData:
{{(function(){
    const userData = getAllUsersDetails.data.data
    const orgData = {}
    getOrgNames.data.data.forEach((org) => {
        orgData[org.id] = org.name;
    });
    orgData["default"] = "none";
    return userData.map((user,i) => {

            const optionals = {}
            if (lastLoginCheckbox.isChecked) {
                optionals["Last_Login"] = user?.last_login || 'never';
            }
            if (loginsCountCheckbox.isChecked) {
                optionals["Logins_Count"] = user?.logins_count || 0;
             }
             if (idCheckbox.isChecked) {
                optionals.UserId = user?.user_id ;
             }
             const rowData = {
                Row: i + 1,
                Name: user.name,
                Email: user.email,
                Blocked: user?.blocked || false,
		        Email_Verified: user?.email_verified || false,
                OrgID: orgData[user?.orgid || 'default'],
                Role: user?.role || '-',
                ...optionals
             }
            return rowData;
         });
})()}}

